<template>    <div class="item-details">        <form method="post" @submit.prevent="onSubmit">            <section class="header">                <div class="control is-horizontal">                    <label class="title">{{ title }}</label>                </div>            </section>            <section class="content">                <section class="content-body">                    <field v-for="item in data" v-if="item.label !== ''" :label="item.label">                        <input class="input" :placeholder="item.placeholder" :type="item.type ? item.type : 'text'" :value="item.value" @input="item.value = $event.target.value">                    </field>                    <slot></slot>                </section>                <section class="content-footer">                    <field>                        <button type="submit" class="button is-primary" :class="{ 'is-disabled': hasErrors() }">Salvar</button>                    </field>                </section>            </section>        </form>    </div></template><script>    import Field from './Field.vue';    import ItemLine from '../item/ItemLine.vue';    export default {        components: { Field, ItemLine },        props: {            title: {                required: true,                type: String,                default: ''            },            data: {                required: true,                type: Array            }        },        methods: {            isEmpty(v) {                return v.length === 0;            },            hasErrors() {                return false;            },            onSubmit() {                let fields = {};                for(let key in this.data) {                    fields[this.data[key].prop] = this.data[key].value;                }                let form = new Form(fields);                this.$emit('submit', form);            }        }    }</script>