<template>    <div class="item-details">        <form method="post" @submit.prevent="onSubmit">            <section class="header">                <div class="control is-horizontal">                    <label class="title">{{ title }}</label>                </div>            </section>            <section class="content">                <section class="content-body">                    <div class="field is-horizontal" v-for="item in data" v-if="item.label !== ''">                        <div class="field-label">{{ item.label }}</div>                        <div class="field-body">                            <div class="field">                                <div class="control">                                    <input :type="item.type ? item.type : 'text'" class="input" :value="item.value" @input="item.value = $event.target.value">                                </div>                            </div>                        </div>                    </div>                    <slot></slot>                </section>                <section class="content-footer">                    <div class="field is-horizontal">                        <div class="field-label"></div>                        <div class="field-body">                            <div class="field">                                <div class="control">                                    <button type="submit" class="button is-primary" :class="{ 'is-disabled': hasErrors() }">Salvar</button>                                </div>                            </div>                        </div>                    </div>                </section>            </section>        </form>    </div></template><script>    import ItemLine from '../item/ItemLine.vue';    export default {        components: { ItemLine },        props: {            title: {                required: true,                type: String,                default: ''            },            data: {                required: true,                type: Array            }        },        methods: {            isEmpty(v) {                return v.length === 0;            },            hasErrors() {                return false;            },            onSubmit() {                let fields = {};                for(let key in this.data) {                    fields[this.data[key].prop] = this.data[key].value;                }                let form = new Form(fields);                this.$emit('submit', form);            }        }    }</script>